function reloadchat(c,b){var a=$(".btn-send-comment").data("url");$.ajax({url:a,type:"POST",data:{message:c},success:function(d){if(b==true){$("#chat_message").val("")}$("#chat-box").html(d)}})}setInterval(function(){reloadchat("",false)},2000);$(".btn-send-comment").on("click",function(){var a=$("#chat_message").val();reloadchat(a,true);});
